import "./style.css"

export default function Caroussel(props) {
    const imgs = props.imgs
    var page = 0

    return (
        <div className="caroussel">
            {imgs.map((items, key) => {
                if (key == props.page) {
                    return (
                        <img key={key} className="carousselImg" src={items}></img>
                    )
                }
                return (
                    <button key={key} onClick={function () {page <= items.length ? page++ : page = 0}}></button>
                )
            })}
        </div>
    )
}

