import "./style.css"

export default function Caroussel(props) {
    const imgs = props.imgs
    var page = 0

    return (
        <div className="caroussel">
            {imgs.map((items, key) => {
                <updateCaroussel items={items} key={key} lol={page} />
            })}
            return (
                    <button key={key} onClick={function () {page <= items.length ? page++ : page = 0 && updateCaroussel(items, key, page)}}></button>
                )
        </div>
    )
}

export function updateCaroussel(items, key, lol) {
    if (key == lol) {
        return (
            <img key={key} className="carousselImg" src={items}></img>
        )
    }
}
