import "./style.css"
import arrow from "../../images/arrow.svg"

var page = 0
var length = 0

export default function Caroussel(props) {
    const imgs = props.imgs

    return (
        <div className="caroussel">
            {imgs.map((items, key) => {
                if (key == page) {
                    return (
                        <div>
                            <div  className="carousselImg"><img key={key} className="carousselImg" src={items}></img>
                                <div onClick={() => {Inc()}} className="arrow"><img src={arrow}></img></div>
                            </div>
                        </div>
                    )
                }

                length++
            })}
            {console.log(length)}
        </div>
    )

    const [currentIndex, setCurrentIndex] = useState(0);

    const handleClickPrev = () => {
      setCurrentIndex(currentIndex === 0 ? images.length - 1 : currentIndex - 1);
    };

    const handleClickNext = () => {
      setCurrentIndex(currentIndex === images.length - 1 ? 0 : currentIndex + 1);
    };

    return (
      <div className="carousel">
        <button onClick={handleClickPrev}>Prev</button>
        <img className="carousselImg" src={imags[currentIndex]} alt={`Image ${currentIndex}`} />
        <button onClick={handleClickNext}>Next</button>
      </div>
    );
};
